mapper.select:
SELECT P.id, P.simple_name name, P.accumulated_income, M.ID manager_id, M.NAME manager_name, M.LOGO manager_logo, P.now_rate, P.one_rate, P.tow_rate, C.name company_name, M.REVIEW manager_review FROM PE_PRODUCT P, PE_MANAGER_PRODUCT MP, PE_MANAGER M, PE_COMPANY C WHERE P.id = MP.product_id AND MP.MANAGER_ID = M.ID AND M.company_id = C.id AND P.status = 2
AND P.fund_type=#{peType}
ORDER BY P.ACCUMULATED_INCOME DESC LIMIT 0,10

SELECT P.id, P.simple_name name, P.accumulated_income, P.net_worth, P.run_time, P.year_rate, M.ID manager_id, M.NAME manager_name, M.LOGO manager_logo, P.now_rate, P.one_rate, P.tow_rate, C.name company_name, M.REVIEW manager_review FROM PE_PRODUCT P, PE_MANAGER_PRODUCT MP, PE_MANAGER M, PE_COMPANY C WHERE P.id = MP.product_id AND MP.MANAGER_ID = M.ID AND M.company_id = C.id
AND P.status = 2 AND (P.status = 2 OR P.status = 3)
AND M.ID=#{peManagerId}
AND C.ID=#{peCompanyId}
AND P.fund_type=#{peType}
AND P.year_rate >= 50 AND P.year_rate >= 30 AND P.year_rate < 50 AND P.year_rate >= 10 AND P.year_rate < 30 AND P.year_rate >= 0 AND P.year_rate < 10 AND P.year_rate < 0
AND (P.name like CONCAT('%',#{keyword},'%') OR C.name like CONCAT('%',#{keyword},'%'))
ORDER BY ${sortField} ${sortType}
ORDER BY P.ACCUMULATED_INCOME DESC
LIMIT #{limitStart} ,#{pageSize}

SELECT count(P.id) c FROM PE_PRODUCT P, PE_MANAGER_PRODUCT MP, PE_MANAGER M, PE_COMPANY C WHERE P.id = MP.product_id AND MP.MANAGER_ID = M.ID AND M.company_id = C.id
AND P.status = 2 AND (P.status = 2 OR P.status = 3)
AND M.ID=#{peManagerId}
AND C.ID=#{peCompanyId}
AND P.fund_type=#{peType}
AND P.year_rate >= 50 AND P.year_rate >= 30 AND P.year_rate < 50 AND P.year_rate >= 10 AND P.year_rate < 30 AND P.year_rate >= 0 AND P.year_rate < 10 AND P.year_rate < 0
AND (P.name like CONCAT('%',#{keyword},'%') OR C.name like CONCAT('%',#{keyword},'%'))
